mapper.select:
select id, name, `desc`, price, picture, discount, order_num, category_id from
(select goods_sku.good_id from (select sku_id from sku_properties where sku_properties.properties_id in
#{attr.propertiesId}
group by sku_id having count(properties_id) >= #{attrsLength} ) skuid left join goods_sku on goods_sku.id = skuid.sku_id) goodid left join goods on goodid.good_id = goods.id where category_id = #{categoryId}
goods where category_id = #{categoryId}
limit #{page} , #{size}
